CREATE TABLE "location" (	"locationId" INTEGER PRIMARY KEY AUTOINCREMENT, 	"name" VARCHAR (255), 	"latitude" REAL, 	"longitude" REAL, 	"lastTwitterSearch" DATETIME);CREATE TABLE "history" (	"historyId" INTEGER PRIMARY KEY AUTOINCREMENT, 	"sessionId" VARCHAR (255), 	"locationId" INTEGER REFERENCES "location" ("locationId"), 	"timestamp" DATETIME DEFAULT current_timestamp);CREATE TABLE "tweet" (	"tweetId" INTEGER PRIMARY KEY AUTOINCREMENT, 	"locationId" INTEGER REFERENCES "location" ("locationId"), 	"user" VARCHAR (50), 	"profileImageUrl" VARCHAR (255), 	"content" VARCHAR (255), 	"latitude" REAL, 	"longitude" REAL, 	"posted" DATETIME);